function hots_parse(url,html) 
{
    var $ = cheerio.load(html);  
    var list = [];
    var c = $("[class='nag cf']");
    c.find("a.clip-link").each(function()
    {
        var a=$(this);
        var bm = {};
        bm.name = a.attr("title");
        bm.url = a.attr("href");
        bm.logo = a.children().children().attr("src");

        list.push(bm);
    });
    return JSON.stringify(list);
}

